INSERT INTO `ft_job` VALUES (81, '仓库管理员', 1, 2, '正社员', 0, 5, NULL, '千葉県 船橋市 南本町33-13', NULL, NULL, NULL, 3, '船橋徒歩15分钟', 0, 1, 0, '22万~(月給)', NULL, '1,2,5,6', NULL, '【工作内容】\n1仓库管理，包括点收，检查，入库保存，发货和盘点\n2 伝票处理，电脑入力,一般事務業務，客人対応（电话，邮件）\n\n【要求】 \n1、有责任心和耐心，务实敬业，日语日常会话，好学能吃苦耐劳\n2、熟悉常用办公软件\n3、有良好的团队合作精神\n※有免許者大欢迎\n \n【待遇 】\n正社員22万～\n\n【研修期】\n3个月(21万)（午休1小时）\n\n【时间】\n9：00～19:00\n月曜日～土曜日\n\n【工作地址】\n船桥市', '2022-07-30 00:00:00', NULL, 2, '興洋国際商事株式会社');
