INSERT INTO `ft_job` VALUES (387, '事务职（活动策划）', 1, 2, '正社员', 0, 2, NULL, '東京都 新宿区 下落合４丁目21-9 目白LKビル６Ｆ', NULL, NULL, NULL, 4, '目白徒歩10分钟', 0, 0, 0, '20万-26万(月給)', NULL, '1,2,5,6,7', NULL, '勤务时间:\n可商议\n\n主要负责学生活动的策划、组织和联络，以及留学、考学方面直播内容的策划和执行\n要求：\n1、性格热情主动，擅交流\n2、有日本留学经历，学士及以上学历\n3、有直播策划经验，有团体活动组织经验\n\n\n招聘人数: 不限', '2022-07-30 00:00:00', NULL, 2, '致遠教育株式会社');
INSERT INTO `ft_job` VALUES (388, '事务，总务', 1, 2, '正社员', 0, 2, NULL, '東京都 台東区 浅草6-36-18', NULL, NULL, NULL, 4, '浅草徒歩15分钟', 0, 0, 0, '面议(月給)', NULL, '1,2,5,6', NULL, '勤务时间:\n10：00～18：00\n\n1，翻译工作\n2，办公司一般事务工作，总务管理\n3，各种票据，书类整理\n4，电话对应\n5，宣传资料制成\n\n\n招聘人数: 5名', '2022-07-30 00:00:00', NULL, 2, '株式会社Revalm');
