INSERT INTO `ft_job` VALUES (534, '事務員', 0, 2, '契约社员', 0, 4, NULL, '埼玉県 さいたま市西区 南荻島2723-1', NULL, NULL, NULL, 3, '北越谷徒歩15分钟', 0, 1, 0, '1100-1200(時給)', NULL, '1,2,5', NULL, '勤务时间:\n8:00-17:00 时间可相谈\n\n1.日语好的会财会的事務員！男性优先\n2.会使用excel，word等办公软件。\n3.处理会社一般事务，无经验可，有工作经验者优先。\n4.管理人员和业务的，能做見積もり的优先。\n5.有免许者优先。\n签证不限\n\n薪资：有经验的1200，入社25万', '2022-07-30 00:00:00', NULL, 2, '東進株式会社');
