INSERT INTO `ft_job` VALUES (1087, '直播买手/淘宝主播/神奈川県/全职兼职可', 1, 5, '正社员', 0, 3, NULL, '神奈川県 横浜市西区 南幸１−６−３１', NULL, NULL, NULL, 1, '横浜徒歩1分钟', 0, 1, 0, '30万-50万(月給)', NULL, '1,2,5,6', NULL, '勤务时间:\n11.00-20.00，周5回\n\n勤务时间：11：00-20：00，周五回\n工作地点：各大商圈\n工作内容：直播商品带货\n福利待遇：可提供签证，交通费全额报销，正规公司交年金和保险。\n薪水工资：面议，底薪加提成，平均月收30-50万，有能力者100万+\n\n简单总结一下这个工作：\n优点：赚的多，正常工作的几倍工资，能接触当下比较火的行业。\n缺点：不是人人都适合，需要头脑灵活，不怯场，不怕辛苦，自律。\n\n没经验也没关系，主要态度端正，认真肯干，业务方面可以教。\n奥特莱斯附近优先，周末可以出勤优先。\n有超过5年以上的直播运营经验，欢迎你的加入。\n\n招聘人数: 3名', '2022-07-30 00:00:00', NULL, 2, '株式会社ブロンズナイト');
INSERT INTO `ft_job` VALUES (1088, '诚聘童装女装主播', 1, 5, '正社员', 0, 2, NULL, '東京都 千代田区 関東', NULL, NULL, NULL, 3, '', 0, 1, 0, '面议(時給)', NULL, '1,2,5,6,7', NULL, '募集主播\n工作地点：関東地区\n高薪诚聘童装女主播，周出勤5日（可选休）\n时给 or 月给 + 奖金 +提成（独立成手）\n雇佣形態：社员 or バイト\n工作内容：淘宝平台直播、童装搭配销售 \n基本要求：\n童装：了解一些童装品牌，懂得应季换搭、小朋友的发育成长速度，突出展示童装的匹配概念。\n中国语普通话流利，日本语口语好、交流无障碍即可。\n\n女装：形象清甜优美，具有亲和力，自我管理能力强。\n情商高、性格开朗自信、虚心学习勤奋有干劲。\n※ 风趣幽默会讲段子 ，愿意放下身段逗周围朋友笑，开心果体质，为最佳~！\n  \n出勤日可以调整', NULL, NULL, 2, '祐徳商事株式会社');
