INSERT INTO `ft_job` VALUES (1264, 'Cobol技术人员', 1, 7, '正社员', 0, 2, NULL, '東京都 中央区 晴海13-2-306', NULL, NULL, NULL, 3, '月島徒歩5分钟', 0, 0, 0, '面议(月給)', NULL, '1,2,5,6,7', NULL, '招聘Cobol技术人员做BSE，系统维护，长期稳定。\n技术要求：\n1.熟练掌握cobol开发语言；\n2.日语流畅，能够读懂日文式样书，能够写单体测试式样书；\n3.工作认真负责，积极主动，具有较强的团队合作精神及沟通能力；\n4.行业经验3年以上。\n\n雇佣方式灵活，可全职，兼职，远程办公！', '2022-07-30 00:00:00', NULL, 2, '金城株式会社');
