INSERT INTO `ft_job` VALUES (1484, 'SAP/Salesforce入门IT新人培训', 1, 7, '正社员', 0, 2, NULL, '東京都 豊島区 東京駅', NULL, NULL, NULL, 3, '東京徒歩10分钟', 0, 0, 0, '25万-50万(月給)', NULL, '1,2,5,6,7', NULL, '勤务时间:\n9時～18時（1日8時間）完全週休2日制（土日祝）\n\n培训募集\n培训期间：3个月左右（根据个人的学习能力提前或延长）\n培训对象：对IT行业感兴趣，刻苦钻研者\n培训内容：SAP ABAP开发/FICO/SD/MM等模块\n培训讲师：名校毕业，20多年世界500强IT企业工作经验。\n\n要求\n教育背景：本科以上学历或者与IT相关的大专以上学历\n语言：日语口语流畅，交流能力强\n其他：责任心强，能吃苦，善于学习新的知识。\n逻辑思维清晰，服从安排。\n\n福利待遇：\n雇佣形式：正社员或者契约社员（根据个人需求可相谈）\n各种资格证取得补助\n社会保险完善\n雇佣保险\n每年员工团建，聚餐等活动完善\n\n招聘人数: 3名', '2022-07-30 00:00:00', NULL, 2, '易拓科技');
