INSERT INTO `ft_job` VALUES (1634, '成手木工，一户建外壁外構、涂装喷漆工', 1, 8, '正社员', 0, 5, NULL, '千葉県 千葉市美浜区 高浜1-3-4', NULL, NULL, NULL, 2, '', 0, 0, 0, '50万(月給)', NULL, '2,4,5,6', NULL, '募集\n因业务扩大 现招一户建成手木工若干名。 按能力给，也可自己包活\n待遇极高！工资详谈！留学，特定技能可以给签证\n房盖上瓦。以一户建为主。成手 半成手。无经验也可。\n一户建外壁工。 若干名。有无经验均可\n一户建外構。 抹水泥地面 等。\n涂装喷漆工。 成手有经验者优先\n以上工种黑户免扰 家族以上签证\n正规会社各种待遇均有\n以上各工种均可给签证。 待遇面议\n本广告长期有效', NULL, NULL, 2, '旺龍工業株式会社');
