INSERT INTO `ft_job` VALUES (1873, '大型免许司机', 1, 11, '正社员', 0, 5, NULL, '千葉県 千葉市中央区 千葉、神奈川、埼玉、茨城', NULL, NULL, NULL, 2, '', 0, 1, 0, '1.5万-1.6万(日給)', NULL, '1,2,5', NULL, '招聘形式：正社员雇佣\n招聘人员：10名\n工作地点范围：千叶、神奈川、茨城、琦玉\n要求持有日本大型证＋牵引证，有无经验均可\n\n工资待遇\n1.有经验者日给16000日元，工作半年后根据工作表现涨薪\n2. 没有经验者日给15000日元，工作半年后涨薪到16000\n3. 参考目前人员基本工资45～55万日元，有涨薪、奖金、升职空间\n4.需要提供住宿的，一间房1-2个人，住宿以及水电煤气费总共30000日元，吃自理\n5.正常缴纳社会保险、劳灾保险等，享受日本福利待遇\n6 工作适应后提供工作手机或者根据现场需要提供车辆', NULL, NULL, 2, '株式会社伸悦');
