INSERT INTO `ft_job` VALUES (1876, '大型、中型货车司机', 1, 11, '正社员', 0, 2, NULL, '東京都 足立区 花畑6丁目', NULL, NULL, NULL, 3, '谷塚バス30分钟', 0, 1, 0, '面议(月給)', NULL, '1,2,5,6,7', NULL, '招聘信息 诚心邀请持有日本大型、中型驾照的小伙伴加入我们的大家庭\n\n工作内容\n配送亚马逊仓库以及仓库对仓库\n保税仓取货配送各个仓库\n工作简单、稳定\n\n待遇方面\n公司提供签证、社保年金、奖金以及交通费\n工资待遇面谈\n\n公司地点\n东京都足立区\n\n\n招聘人数: 2名', '2022-07-30 00:00:00', NULL, 2, 'NTK観光バス株式会社');
