INSERT INTO `ft_resume` VALUES (7, '爱新觉罗 玄烨', 'めいちゅあんくづ', 1, '2000-08-17', '08033028999', 'shadow', '111111111@qq.com', '東京都', '江戸川区', '江戸川区西葛西１丁目１４', '都营新宿线', '船堀駅', 1, '技術・人文知識・国際業務', '1~3年', 0, '有り', '初級', 'N1', '博士', '江苏', '在家赋闲', '新东方烹饪学校', '1998-02', '2013-12', 'Master', '情報処理科', '索尼株式会社', '2010-02', '2023-12', '映画鑑賞', 'パソコンの修理', '自己紹介は「1分」と時間指定をして求められることも少なくありません。また特に指定がない場合も、自己紹介は1分程度が基本となります。そしてこの1分間で第一印象は大きく左右されるため、この短い自己紹介の時間を制することができれば、その後の面接の流れを掴みやすくなるでしょう。', '現在はIT系のハードウェアベンダーで、サーバ製品の営業を担当しています。現在担当している製品にも、営業の仕事自体にも不満はないのですが、顧客企業は大手企業で、大規模なシステムの一部としてサーバを導入する形です。ほかの製品やアプリケーションを担当する営業メンバー数十人との協業となるため、私個人が提供する価値や貢献度が見えにくいことに割り切れなさを感じています。中小企業向けにWebサービスを提供している御社では、個人または少人数のチームで営業に当たれると知り、関心を持ちました。ITインフラの知識を活かしながら、自分の提供価値が見えやすい環境で働きたいと思い、御社を志望しました。', 1, '');
